// ShoppingLists.js
import React, { useState } from 'react';

const ShoppingLists = ({ lists, onDeleteList, onViewList, onCreateList }) => {
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newListName, setNewListName] = useState('');

  const handleCreateList = () => {
    // Validate newListName if needed
    onCreateList(newListName);
    setNewListName('');
    setShowCreateForm(false);
  };

  return (
    <div>
      <h2>Shopping Lists</h2>
      {lists.map(list => (
        <div key={list.id} className="list-tile">
          <span onClick={() => onViewList(list.id)}>{list.name}</span>
          {list.owner && <button onClick={() => onDeleteList(list.id)}>Delete</button>}
        </div>
      ))}
      <button onClick={() => setShowCreateForm(true)}>Create New Shopping List</button>

      {showCreateForm && (
        <div className="modal">
          <input type="text" placeholder="New Shopping List Name" value={newListName} onChange={(e) => setNewListName(e.target.value)} />
          <button onClick={handleCreateList}>Create</button>
          <button onClick={() => setShowCreateForm(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default ShoppingLists;
