// ShoppingListDetail.js
import React, { useState } from 'react';

const ShoppingListDetail = ({
  list,
  onEditListName,
  onAddRemoveMember,
  onLeaveList,
  onAddRemoveItem,
  onSetItemResolved,
}) => {
  const [editingName, setEditingName] = useState(false);
  const [newName, setNewName] = useState(list.name);
  const [addingMember, setAddingMember] = useState(false);
  const [newMember, setNewMember] = useState('');
  const [addingItem, setAddingItem] = useState(false);
  const [newItem, setNewItem] = useState('');

  const handleEditListName = () => {
    setEditingName(true);
  };

  const handleSaveListName = () => {
    onEditListName(newName);
    setEditingName(false);
  };

  const handleCancelEditListName = () => {
    setNewName(list.name);
    setEditingName(false);
  };

  const handleAddRemoveMember = () => {
    onAddRemoveMember(newMember);
    setNewMember('');
  };

  const handleLeaveList = () => {
    onLeaveList();
  };

  const handleAddRemoveItem = () => {
    onAddRemoveItem(newItem);
    setNewItem('');
  };

  const handleSetItemResolved = (itemId) => {
    onSetItemResolved(itemId);
  };
  return (
    <div>
      <h2>{editingName ? <input value={newName} onChange={(e) => setNewName(e.target.value)} /> : list.name}</h2>

      <div>
        <h3>Members</h3>
        <ul>
          {list.members.map((member) => (
            <li key={member.id}>{member.name}</li>
          ))}
        </ul>
        {addingMember && (
          <div>
            <input value={newMember} onChange={(e) => setNewMember(e.target.value)} />
            <button onClick={handleAddRemoveMember}>Add Member</button>
          </div>
        )}
        <button onClick={() => setAddingMember(!addingMember)}>Add/Remove Member</button>
      </div>

      <div>
        <h3>Items</h3>
        <ul>
          {list.items.map((item) => (
            <li key={item.id}>
              {item.name} {!item.resolved && <button onClick={() => handleSetItemResolved(item.id)}>Resolve Item</button>}
            </li>
          ))}
        </ul>
        {addingItem && (
          <div>
            <input value={newItem} onChange={(e) => setNewItem(e.target.value)} />
            <button onClick={handleAddRemoveItem}>Add Item</button>
          </div>
        )}
        <button onClick={() => setAddingItem(!addingItem)}>Add Item</button>
      </div>

      <button onClick={handleEditListName}>{editingName ? 'Save List Name' : 'Edit List Name'}</button>
      {editingName && <button onClick={handleCancelEditListName}>Cancel</button>}
      <button onClick={handleLeaveList}>Leave Shopping List</button>
    </div>
  );
};

export default ShoppingListDetail;
