// ShoppingListDetail.js
import React, { useState } from 'react';

const ShoppingListDetail = ({
  list,
  onEditListName,
  onAddRemoveMember,
  onLeaveList,
  onAddItem,
  onRemoveItem,
  onSetItemResolved,
}) => {
  const [showEditListNameForm, setShowEditListNameForm] = useState(false);
  const [newListName, setNewListName] = useState(list.name);

  const handleEditListName = () => {
    onEditListName(newListName);
    setShowEditListNameForm(false);
  };

  const handleCancelEdit = () => {
    setShowEditListNameForm(false);
  };

  const handleAddRemoveMember = () => {
    onAddRemoveMember(); // You may want to pass parameters or implement logic based on your requirements
  };

  const handleLeaveList = () => {
    onLeaveList(); // Implement logic to leave the list
  };

  const handleAddItem = () => {
    onAddItem(); // Implement logic to add an item
  };

  const handleRemoveItem = (itemId) => {
    onRemoveItem(itemId); // Implement logic to remove an item
  };

  const handleSetItemResolved = (itemId) => {
    onSetItemResolved(itemId); // Implement logic to set an item as resolved
  };

  return (
    <div>
      <h2>{list.name}</h2>
      <button onClick={() => setShowEditListNameForm(true)}>Edit List Name</button>
      <button onClick={handleAddRemoveMember}>Add/Remove Member</button>
      <button onClick={handleLeaveList}>Leave Shopping List</button>
      <button onClick={handleAddItem}>Add Item</button>

      {list.items.map((item) => (
        <div key={item.id} className="item">
          <span>{item.name}</span>
          {!item.resolved && (
            <>
              <button onClick={() => handleRemoveItem(item.id)}>Remove Item</button>
              <button onClick={() => handleSetItemResolved(item.id)}>Resolve Item</button>
            </>
          )}
        </div>
      ))}

      {showEditListNameForm && (
        <div className="modal">
          <h3>Edit List Name</h3>
          <input
            type="text"
            placeholder="Enter New List Name"
            value={newListName}
            onChange={(e) => setNewListName(e.target.value)}
          />
          <button onClick={handleEditListName}>Save</button>
          <button onClick={handleCancelEdit}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default ShoppingListDetail;
