// ShoppingListDetail.js
import React, { useState } from 'react';

const ShoppingListDetail = ({
  list,
  onEditListName,
  onAddRemoveMember,
  onLeaveList,
  onAddRemoveItem,
  onSetItemResolved,
}) => {
  const handleEditListName = () => {
    onEditListName(); // Add your logic for editing list name
  };

  const handleAddRemoveMember = () => {
    onAddRemoveMember(); // Add your logic for adding/removing members
  };

  const handleLeaveList = () => {
    onLeaveList(); // Add your logic for leaving the shopping list
  };

  const handleAddRemoveItem = () => {
    onAddRemoveItem(); // Add your logic for adding/removing items
  };

  const handleSetItemResolved = (itemId) => {
    onSetItemResolved(itemId); // Add your logic for resolving an item
  };

  return (
    <div>
      <h2>{list.name}</h2>
      <button onClick={handleEditListName}>Edit List Name</button>
      <button onClick={handleAddRemoveMember}>Add/Remove Member</button>
      <button onClick={handleLeaveList}>Leave Shopping List</button>

      <ul>
        {list.items.map((item) => (
          <li key={item.id}>
            {item.name}
            <button onClick={() => handleSetItemResolved(item.id)}>Resolve Item</button>
          </li>
        ))}
      </ul>

      <button onClick={handleAddRemoveItem}>Add/Remove Item</button>
    </div>
  );
};

export default ShoppingListDetail;
