// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ShoppingLists from './components/ShoppingLists';
import ShoppingListDetail from './components/ShoppingListDetail';
import './App.css';

const App = () => {
  const [lists, setLists] = useState([
    {
      id: 1,
      name: 'Shopping List 1',
      owner: 'Member 1',
      items: [
        { id: 1, name: 'Item 1', resolved: false },
        { id: 2, name: 'Item 2', resolved: false },
      ],
    },
    // Add more shopping lists as needed
  ]);

  const [selectedList, setSelectedList] = useState(null);

  const handleViewList = (listId) => {
    const selected = lists.find(list => list.id === listId);
    setSelectedList(selected);
  };

  const handleDeleteList = (listId) => {
    setLists(prevLists => prevLists.filter(list => list.id !== listId));
    setSelectedList(null);
  };

  const handleCreateList = (listName) => {
    const newList = {
      id: lists.length + 1,
      name: listName,
      owner: 'Current User', // You need to replace this with the actual owner
      items: [],
    };

    setLists(prevLists => [...prevLists, newList]);
  };
  const findListIndexById = (id) => lists.findIndex((list) => list.id === id);

  const handleEditListName = (listId, newName) => {
    const index = findListIndexById(listId);
    if (index !== -1) {
      setLists((prevLists) => {
        const updatedLists = [...prevLists];
        updatedLists[index].name = newName;
        return updatedLists;
      });
    }
  };

  const handleAddRemoveMember = (listId, memberId, action) => {
    const index = findListIndexById(listId);
    if (index !== -1) {
      setLists((prevLists) => {
        const updatedLists = [...prevLists];
        const list = updatedLists[index];

        if (action === 'add') {
          list.members = list.members || [];
          list.members.push(memberId);
        } else if (action === 'remove') {
          list.members = list.members.filter((member) => member !== memberId);
        }

        return updatedLists;
      });
    }
  };

  const handleLeaveList = (listId, memberId) => {
    const index = findListIndexById(listId);
    if (index !== -1) {
      setLists((prevLists) => {
        const updatedLists = [...prevLists];
        const list = updatedLists[index];

        if (list.owner === memberId) {
          // If owner is leaving, consider logic to handle ownership transfer
        }

        list.members = list.members.filter((member) => member !== memberId);
        return updatedLists;
      });
    }
  };

  const handleAddRemoveItem = (listId, action, newItem) => {
    const index = findListIndexById(listId);
    if (index !== -1) {
      setLists((prevLists) => {
        const updatedLists = [...prevLists];
        const list = updatedLists[index];

        if (action === 'add') {
          list.items.push({ id: list.items.length + 1, name: newItem, resolved: false });
        } else if (action === 'remove') {
          // Implement logic for removing items as needed
        }

        return updatedLists;
      });
    }
  };

  const handleSetItemResolved = (listId, itemId) => {
    const index = findListIndexById(listId);
    if (index !== -1) {
      setLists((prevLists) => {
        const updatedLists = [...prevLists];
        const list = updatedLists[index];

        const itemIndex = list.items.findIndex((item) => item.id === itemId);
        if (itemIndex !== -1) {
          list.items[itemIndex].resolved = true;
        }

        return updatedLists;
      });
    }
  };

  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <ShoppingLists lists={lists} onDeleteList={handleDeleteList} onViewList={handleViewList} onCreateList={handleCreateList} />
        </Route>
        <Route path="/list/:id">
          {({ match }) => {
            const listId = parseInt(match.params.id, 10);
            const selectedList = lists.find((list) => list.id === listId);
            return selectedList ? (
              <ShoppingListDetail
                list={selectedList}
                onEditListName={() => {}}
                onAddRemoveMember={() => {}}
                onLeaveList={() => {}}
                onAddRemoveItem={() => {}}
                onSetItemResolved={() => {}}
              />
            ) : (
              <p>List not found</p>
            );
          }}
        </Route>
      </Switch>
    </Router>
  );
};

export default App;
