// ShoppingListDetail.js
import React, { useState } from 'react';

const ShoppingListDetail = ({ list, onEditListName, onAddRemoveMember, onLeaveList, onAddRemoveItem }) => {
  const [showEditListName, setShowEditListName] = useState(false);
  const [showAddRemoveMember, setShowAddRemoveMember] = useState(false);
  const [showLeaveList, setShowLeaveList] = useState(false);
  const [showAddItem, setShowAddItem] = useState(false);

  const handleEditListName = () => {
    setShowEditListName(true);
  };

  const handleAddRemoveMember = () => {
    setShowAddRemoveMember(true);
  };

  const handleLeaveList = () => {
    setShowLeaveList(true);
  };

  const handleAddItem = () => {
    setShowAddItem(true);
  };

  const closePopup = () => {
    setShowEditListName(false);
    setShowAddRemoveMember(false);
    setShowLeaveList(false);
    setShowAddItem(false);
  };

  return (
    <div>
      <h2>{list.name}</h2>
      <ul>
        {list.items.map((item) => (
          <li key={item.id}>
            {item.name} {item.resolved ? '(Resolved)' : ''}
          </li>
        ))}
      </ul>
      <button onClick={handleEditListName}>Edit List Name</button>
      <button onClick={handleAddRemoveMember}>Add/Remove Member</button>
      <button onClick={handleLeaveList}>Leave Shopping List</button>
      <button onClick={handleAddItem}>Add Item</button>

      {/* Edit List Name Popup */}
      {showEditListName && (
        <div className="popup">
          <h3>Edit List Name</h3>
          {/* Add form elements for editing the list name */}
          <button onClick={closePopup}>Close</button>
        </div>
      )}

      {/* Add/Remove Member Popup */}
      {showAddRemoveMember && (
        <div className="popup">
          <h3>Add/Remove Member</h3>
          {/* Add form elements for adding/removing members */}
          <button onClick={closePopup}>Close</button>
        </div>
      )}

      {/* Leave List Popup */}
      {showLeaveList && (
        <div className="popup">
          <h3>Leave Shopping List</h3>
          <p>Are you sure you want to leave this shopping list?</p>
          <button onClick={closePopup}>Cancel</button>
          <button onClick={onLeaveList}>Leave</button>
        </div>
      )}

      {/* Add Item Popup */}
      {showAddItem && (
        <div className="popup">
          <h3>Add Item</h3>
          {/* Add form elements for adding an item */}
          <button onClick={closePopup}>Close</button>
        </div>
      )}
    </div>
  );
};

export default ShoppingListDetail;
