// ShoppingListDetail.js
import React from 'react';
import ListItem from './ListItem';

const ShoppingListDetail = ({ list, onEditListName, onAddRemoveMember, onLeaveList, onAddRemoveItem, onSetItemResolved }) => {
  return (
    <div>
      <h2>{list.name}</h2>
      <p>Owner: {list.owner}</p>

      <h3>Items</h3>
      {list.items.map(item => (
        <ListItem
          key={item.id}
          item={item}
          onAddRemoveItem={() => onAddRemoveItem(list.id, item.id)}
          onSetItemResolved={() => onSetItemResolved(list.id, item.id)}
        />
      ))}

      <button onClick={onEditListName}>Edit List Name</button>
      <button onClick={onAddRemoveMember}>Add/Remove Member</button>
      <button onClick={onLeaveList}>Leave Shopping List</button>
    </div>
  );
};

export default ShoppingListDetail;
